$green: #212D2C;
$sky: #A9FDFF;
$cyan: #36C9B4;

.my-color {
  background-color: $green;
  color: $sky;
  padding: 1em;
}

.my-link a {
  @extend .my-color;
  color: $cyan; 

  &:hover {
      background-color: rgba( $green, .5 );  
      color: white;
  }
}

.my-color,.my-link a{background-color:#212D2C;color:#A9FDFF;padding:1em}.my-link a{color:#36C9B4}.my-link a:hover{background-color:rgba(33,45,44,0.5);color:white}


